css:
*{
    margin:0px;
    padding:0px;
}

.print-format {
    padding: 20px;
    margin: 0;
}

.print-format h1{
    padding:0;
    margin:0;
}

span{
    font-size:15px;
}

strong{
     font-size:15px;
}

.print-format h3{
    margin:0;
    padding:0
}

.header{
    position:relative;
    width:60%;
    left:18%;
    height:10vh;
}

.details{
    display: flex;
    flex-direction: row;
    justify-content: space-between;
}

.details-1{
    display:flex;
    flex-direction:row;
    justify-content: space-between;
}

.details-3{
    display:flex;
    flex-direction:row;
    justify-content: space-between;
}
.details-4{
    display:flex;
    flex-direction:row;
    justify-content: space-between;
}

table {
    border-collapse: collapse;
    margin:0px auto 0px auto;
}

th, td {
    width: 90%;
    border: 1px solid #ddd;
    padding: 8px;
    text-align: left;
}

th {
      background-color: #f2f2f2;
}

.attachment{
margin-left:0px;
margin-top:0px;
width:15%;
}

.letter-head{
    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
}

.sign{
    margin-top:10px;
    display:flex;
    justify-content:space-between;
    flex-direction: row;
    width:100%;
}
.sign img{
    width:100%;
    height:5vh;
}
.sign1{
    width:30%;
    height:7vh;
}

.det-margin{
    margin-right:10px;
      width:40%;
}

@media screen and (max-width: 768px) {
    .sign {
        flex-direction: column; /* Display the images in a column */
        align-items: center; /* Center align the items horizontally */
    }

    .sign1 {
        margin-bottom: 20px; /* Increase space between signatures */
    }
}
